import { Component } from 'react'
import { RoomContext } from '../context';
import ChatRoomButton from './ChatRoomButton';

export default class ChatList extends Component {
    static contextType = RoomContext;
    constructor(props) {
        super(props)
        this.state = {
            activeWindow: '',
            checkedChats: []
        }
    }

    componentDidUpdate(prevProps) {

        if ( (prevProps.currentUser !== this.props.currentUser) && this.props.currentUser !== null && this.props.currentUser.rooms.length > 1) {
            const chatWindow = this.props.currentUser.rooms.find(room => room.id !== '992194b2-feaa-4842-a546-5c3482ae69c4' )
            this.context.changeWindow(chatWindow.id)
        }
    }

    switchChat = (event) => {
        this.context.changeWindow(event.target.value)
    }

    addCheckedChat = (chat) => {
        const addedChat = {id: chat}
        this.setState({
            checkedChats: [...this.state.checkedChats, addedChat]
        })
    }

    removeCheckedChat = (chat) => {
        const filtered = this.state.checkedChats.filter(room => room.id !== chat)        
        console.log(filtered)
        this.setState({
            checkedChats: filtered
        })
    }

    deleteChats = () => {

        this.state.checkedChats.map(chat => {
            console.log(chat.id)
        })

        // this.props.currentUser.deleteRoom({ roomId: someRoomID })
        //     .then(() => {
        //         console.log(`Deleted room with ID: ${someRoomID}`)
        //     })
        //     .catch(err => {
        //         console.log(`Error deleted room ${someRoomID}: ${err}`)
        //     })
    }

    seeList = () => {
        console.log(this.state.checkedChats)
    }

    render() {
        const { currentUser } = this.props
        return (
            <>  <button onClick={this.deleteChats} >Delete Chats</button>
                <ul className="chatrooms-container">

                {
                    currentUser ?
                    Object.values(currentUser.rooms).map((room, index) => {
                        if ( room.id !== '765b61eb-ad46-4c8b-bd31-2e4d4acc6f45' ) {
                            return (
                                <ChatRoomButton key={index} room={room} see={this.seeList} onClick={this.switchChat} check={this.addCheckedChat} uncheck={this.removeCheckedChat}  />
                            )
                        }
                    }) : 'Loading...'
                }
                </ul>
            </>
        )
    }
}
