import { useState, useContext, useEffect } from 'react'
import chatImg from '../images/chat.jpg'
import loading from '../images/gif/loading-arrow.gif'
import axios from 'axios';
import { RoomContext } from '../context'
import Link from 'next/link';
import jwt from 'jsonwebtoken';


export default function chat() {
    const context = useContext(RoomContext);
    const [input, setInput] = useState({});
    const [btnDisabled, setbtnDisabled] = useState(false);
    const [errors, setErrors] = useState({});
    const style = {
        backgroundImage: "url("+ chatImg + ")",
        height: 600,
        width: "100%",
        padding: "150px 0 150px 0"
    };
    const handleChange = (event) => setInput({
        ...input,
        [event.target.name]: event.target.value
    });

    useEffect(() => {

        // console.log(context)
        const userData = JSON.parse(localStorage.getItem('userData'));
        const decoded = jwt.decode(userData.token, {complete:true});
        console.log(decoded)
    }, [])

    const handleSubmit = async (event) => {
        event.preventDefault();
        setbtnDisabled(true);
        axios({
            url: 'http://localhost:8000/api/users/login',
            method: 'POST',
            data: JSON.stringify({
                email: input.email,
                password: input.password
            }),
            headers: { 'Content-Type': 'application/json'}
        })
        .then(res => {
            setbtnDisabled(false);
            const userData = JSON.parse(localStorage.getItem('userData'));
            console.log(userData.token);    
            console.log(res.data.token);
            console.log(userData.token === res.data.token)
            context.updateCredentials(res.data.userId, res.data.token);
        })
        .catch(err => {
            console.log(err);
            setErrors({
                ...errors,
                error: "No se pudo conectar. Revise sus credenciales"
            })
            setbtnDisabled(false);
        })
    }
    const renderForm = () => {
        return (
            <React.Fragment>
                <h3>Login to Chat</h3>
                {
                Object.values(errors).map((err, index) => {
                    return <div key={index} className="login-err">{err}</div>
                })
                }
                <form onSubmit={handleSubmit}>
                    <label htmlFor="email">Email</label>
                    <input type="email" name="email" id="email" onChange={handleChange}/>

                    <label htmlFor="password">Password</label>
                    <input type="password" name="password" id="password" onChange={handleChange}/>
                    <button disabled={btnDisabled} className={btnDisabled?"btn-primary submit-btn disabled":"btn-primary submit-btn"} type="submit">{
                        btnDisabled ?
                        <img className="loading-login" src={loading} alt="loading spinner"/>:
                        "Loading"
                    }</button>
                </form>
            </React.Fragment>
        )
    }
    const renderLoggedin = () => {
        return (
            <div>
                <h3>You are logged in</h3>
                <Link href="/">
                    <a className="btn-primary">
                        Go Home
                    </a>
                </Link>
            </div>
        )
    }
    return (
        <div style={style}>
            <div className="log-box">
                {
                    context.authenticated ?
                        renderLoggedin() :
                        renderForm()
                }
            </div>
        </div>
    )
}
