import { Component } from 'react'
import UsernameForm from './UsernameForm'
import ChatWidgetHeader from './ChatWidgetHeader'
import ChatScreen from './ChatScreen'

export default class ChatScreenDisplay extends Component {
    constructor(props) {
        super(props)
    }

    handleChat = () => {
        if ( this.props.currentScreen === 'WhatIsYourUsernameScreen' ) {
            return <UsernameForm onSubmit={this.props.onSubmit}/>         
        } else if ( this.props.currentScreen === 'ChatScreen' ) {
            return <ChatScreen currentUsername={this.props.currentUsername} userId={this.props.userId} />
        }
    }

    render() {
        return (
            <div className="chat-screen-display" >
                <ChatWidgetHeader />
                {this.handleChat()}
            </div>
        )
    }
}
