import React, { Component } from 'react'
import Link from 'next/link'
import { withRouter } from 'next/router'
//Components
// import defaultBcg from '../images/room-1.jpeg'
import Hero from '../../components/Hero'
import Banner from '../../components/Banner'
//Context
import { RoomContext, Room } from '../../context'
import StyledHero from '../../components/StyledHero'


class SingleRoom extends Component {

    // static async getInitialProps({query}) {
    //     // console.log(query)
    //     return {query}
    // }
    constructor(props){
        super(props)
        console.log(this.props.router.query.name)
        this.state={
            slug: this.props.router.query.name,
            // defaultBcg
        }
    }
    static contextType = RoomContext;
    // componentDidMount(){}
    render() {

        const {getRoom} = this.context;
        const room = getRoom(this.state.slug);
        if (!room) {
            return <div className="error">
            <h3>no such room could be found...</h3>
            <Link href='/rooms'>
                <a  className="btn-primary">back to rooms</a>
            </Link>
            </div>
        }
        const { name,description,capacity,size,price,extras,breakfast,pets,images } = room
        const [mainImg,...defaultImg] = images
        return (
            <>
            <StyledHero img={mainImg}>
                <Banner title={`${name} room`}>
                    <Link href="/rooms">
                        <a  className="btn-primary">back to rooms</a>
                    </Link>
                </Banner>
            </StyledHero>
            <section className="single-room">
                <div className="single-room-images">
                { defaultImg.map((item,index) => {
                   return <img key={index} src={item} alt={name}/>
                }) }
                </div>
                <div className="single-room-info">
                    <article className="description">
                        <h3>Details</h3>
                        <p>{description}</p>
                    </article>
                    <article className="info">
                        <h3>Info</h3>
                        <h6>price: ${price}</h6>
                        <h6>size: ${size} sqm</h6>
                        <h6>max capacity: {capacity} pax</h6>
                        <h6>{pets?"pets allows":"no pets allowed"}</h6>
                        <h6>{breakfast && "free breakfast included"}</h6>
                    </article>
                </div>
            </section>
            <section className="room-extras">
                <h6>extras</h6>
                <ul className="extras">
                    {extras.map((item,index) => {
                        return <li key={index}>- {item}</li>
                    })}
                </ul>
            </section>
            </>
        )
    }
}

export default withRouter(SingleRoom);
