import React, { Component } from 'react'
import Chatkit from '@pusher/chatkit-client'
//Components
import MessageList from './MessageList'
import SendMessageForm from './SendMessageForm'
import TypingIndicator from './TypingIndicator'

export default class ChatScreen extends Component {
    constructor(props) {
        super(props)
        this.state = {
            messages: [],
            currentRoom: {},
            currentUser: {},
            userTyping: '',
            isUserTyping: false
        }
    }

    componentDidMount = () => {
        console.log('mounted', this.props.currentUsername);
        console.log('mounted', this.props.userId);
        const chatManager = new Chatkit.ChatManager({
            instanceLocator: 'v1:us1:fe088103-8b4d-4e06-a93c-4d2fb3f963be',
            userId: this.props.userId,
            tokenProvider: new Chatkit.TokenProvider({
                url: 'http://localhost:3001/authenticate'
            })
        })

        chatManager
            .connect()
            .then(currentUser => {
                this.setState({currentUser});
                console.log(currentUser)

                // return currentUser.createRoom({
                //     id: `#${currentUser.id}-room`,
                //     name: `${currentUser.id}-room`,
                //     messageLimit: 100,
                //     hooks: {
                //         onMessage: (message) => {
                //             console.log(message)
                //             this.setState({
                //                 messages: [...this.state.messages, message]
                //             })
                //         },
                //         // onUserStartedTyping: user => {
                //         //     this.setState({
                //         //         userTyping: user.name,
                //         //         isUserTyping: true
                //         //     })
                //         // },
                //         // onUserStoppedTyping: user => {
                //         //     this.setState({
                //         //         userTyping: user.name,
                //         //         isUserTyping: false
                //         //     })
                //         // }
                //     },
                //     private: false,
                //     addUserIds: [currentUser.id, 'guido']
                // }).then(room => {
                //     this.setState({ currentRoom: room })
                //     console.log('created', room)
                // }).catch(err => {
                //     console.log('error', err)
                // })

                return currentUser.subscribeToRoom({
                    roomId: '29333978-d984-447e-b9b8-09c7f183a640',
                    messageLimit: 100,
                    hooks: {
                        onMessage: (message) => {
                            console.log(message)
                            this.setState({
                                messages: [...this.state.messages, message]
                            })
                        },
                        onUserStartedTyping: user => {
                            this.setState({
                                userTyping: user.name,
                                isUserTyping: true
                            })
                        },
                        onUserStoppedTyping: user => {
                            this.setState({
                                userTyping: user.name,
                                isUserTyping: false
                            })
                        }
                    }
                })
            })
            .then(currentRoom => this.setState({currentRoom}))
            .catch(error => console.log('Hiiiii', error))
    }

    sendMessage = (text) => {
        this.state.currentUser.sendSimpleMessage({
            roomId: this.state.currentRoom.id,
            text
        }).then(messageId => console.log('sent', messageId))
        .catch(err => console.log('error', err))
    }

    sendTypingEvent = () => {
        this.state.currentUser.isTypingIn({
            roomId: this.state.currentRoom.id
        }).catch(err => {
            console.log(err)
        })
    }

    render() {
        return (
            <div>
                Hello {this.props.currentUsername}
                <MessageList messages={this.state.messages}/>
                { this.state.isUserTyping ? <TypingIndicator userTyping={this.state.userTyping} /> : '' }
                <SendMessageForm onSubmit={this.sendMessage} onChange={this.sendTypingEvent} />
            </div>
        )
    }
}
