import React, { Component } from 'react'
import Link from 'next/link'
import { withRouter } from 'next/router'
//Components
// import defaultBcg from '../images/room-1.jpeg'
import Banner from '../../components/Banner'
//Context
import { RoomContext, RoomProvider } from '../../context'
import StyledHero from '../../components/StyledHero'
import Loading from '../../components/Loading'
//SEO
import Head from 'next/head';
import axios from 'axios'

class room extends Component {

    static async getInitialProps({query}) {
        return {query}
    }
    constructor(props){
        super(props)
        console.log(this.props.router.query.name)
        this.state={
            slug: this.props.router.query.name,
            // defaultBcg
        }
    }
    static contextType = RoomContext;

    formatData = (items) => {
        console.log('items are', items)
        let tempItems = items.data.map(item => {
            let id = item.id;
            let images = item.images.map(image => image.url);
            let room = {...item,images,id};
            return room;
        });
        return tempItems;
    }

    getRoom = async (slug) => {
        console.log('getting rooms')

        try {
        const response = await axios.get('https://test-project-react.herokuapp.com/hotel-rooms');
        let rooms = this.formatData(response.data);
        let tempRooms = [...rooms];
        const room = tempRooms.find(room => room.slug === slug);
        return room
        } 
        catch(error) {console.log('not getting rooms')}
        return room;
    }

    render() {
        console.log('start render')
        const room = this.getRoom(this.state.slug)
        if (!room) {
            console.log('this should be a room',room)
            return <Loading/>
        }
        const { name,description,capacity,size,price,extras,breakfast,pets,images } = room
        console.log(images)
        const [mainImg,...defaultImg] = images
        return (
            <>
            <Head>
                <title>{name}</title>
                <meta name="title" content={name}/>
                <meta name="description" content={description}/>
                <meta property="og:description" content={description}/>
                <meta property="og:image" content={mainImg}/>
                <meta property="og:title" content={name}/>
            </Head>
            <StyledHero img={mainImg}>
                <Banner title={`${name} room`}>
                    <Link href="/rooms">
                        <a  className="btn-primary">back to rooms</a>
                    </Link>
                </Banner>
            </StyledHero>
            <section className="single-room">
                <div className="single-room-images">
                { defaultImg.map((item,index) => {
                   return <img key={index} src={item} alt={name}/>
                }) }
                </div>
                <div className="single-room-info">
                    <article className="description">
                        <h3>Details</h3>
                        <p>{description}</p>
                    </article>
                    <article className="info">
                        <h3>Info</h3>
                        <h6>price: ${price}</h6>
                        <h6>size: ${size} sqm</h6>
                        <h6>max capacity: {capacity} pax</h6>
                        <h6>{pets?"pets allows":"no pets allowed"}</h6>
                        <h6>{breakfast && "free breakfast included"}</h6>
                    </article>
                </div>
            </section>
            <section className="room-extras">
                <h6>extras</h6>
                <ul className="extras">
                    {extras.map((item,index) => {
                        return <li key={index}>- {item}</li>
                    })}
                </ul>
            </section>
            </>
        )
    }
}

export default withRouter(room);
