import React, { useState, useContext } from 'react'
import chatImg from '../images/chat.jpg'
import loading from '../images/gif/loading-arrow.gif'
import axios from 'axios';
import { RoomContext } from '../context'

export default function chat() {
    const context = useContext(RoomContext);
    const [input, setInput] = useState({});
    const [btnDisabled, setbtnDisabled] = useState(false);
    const [errors, setErrors] = useState({});
    const style = {
        backgroundImage: "url("+ chatImg + ")",
        height: 600,
        width: "100%",
        padding: "150px 0 150px 0"
    };
    const handleChange = (event) => setInput({
        ...input,
        [event.target.name]: event.target.value
    });
    const handleSubmit = async (event) => {
        event.preventDefault();
        setbtnDisabled(true);
        axios({
            url: 'http://localhost:8000/api/users/login',
            method: 'POST',
            data: JSON.stringify({
                email: input.email,
                password: input.password
            }),
            headers: { 'Content-Type': 'application/json'}
        })
        .then(res => {
            setbtnDisabled(false);
            context.updateEmail(res.data.email);
            localStorage.setItem('userData', JSON.stringify({userId: res.data.userId, token: res.data.token}));
        })
        .catch(err => {
            setErrors({
                ...errors,
                error: "No se pudo conectar. Revise sus credenciales"
            })
            setbtnDisabled(false);
        })
    }
    return (
        <div style={style}>
            <div className="log-box">
                <h3>Login to Chat</h3>
                {
                Object.values(errors).map(err => {
                    return <div className="login-err">{err}</div>
                })
                }
                <form onSubmit={handleSubmit}>
                    <label htmlFor="email">Email</label>
                    <input type="email" name="email" id="email" onChange={handleChange}/>

                    <label htmlFor="password">Password</label>
                    <input type="password" name="password" id="password" onChange={handleChange}/>
                    <button disabled={btnDisabled} className={btnDisabled?"btn-primary submit-btn disabled":"btn-primary submit-btn"} type="submit">{
                        btnDisabled ?
                        <img className="loading-login" src={loading} alt="loading spinner"/>:
                        "Loading"
                    }</button>
                </form>
            </div>
        </div>
    )
}
